mapper.select:
select * from picture
where use_status = #{use_status} and user_name=#{user_name} and 
tocken=#{tocken}  and picture_name=#{picture_name} and picture_url=#{picture_url}

mapper.insert:
insert into picture (
    use_status, user_name, user_id, tocken, picture_name,picture_url,create_time
) values (
    #{use_status}, #{user_name}, #{user_id}, #{tocken}, #{picture_name}, #{picture_url}, #{create_time}
)
