mapper.update:
update equipment set
authentiCationStatus=#{authentiCationStatus},authentiCationFailure=#{authentiCationFailure},examineTime=#{examineTime},examineName=#{examineName},
welfareStatus=#{welfareStatus}
where userId=#{userId} and id=#{id}

mapper.select:
select count(*) from equipment where welfareStatus!=3 and
userId=#{userId}
and welfareStatus=#{welfareStatus}   
and releaseType=#{releaseType}   
and releaseType in(18,33,34)

select id,userId,evaluateid,servicFrequenc,releaseType,
releaseTitle,serviceIntroduction,remarks,pictureUrl,serviceType,serviceAndprice,
welfareStatus,authentiCationStatus,authentiCationFailure,
consigneeName,createTime,
updateTime,serviceDetailed,termOfValidity
from equipment
where welfareStatus!=3 and userId=#{userId}
and welfareStatus=#{welfareStatus}   
and releaseType=#{releaseType}   
and releaseType in(18,33,34)  
order by updateTime desc
limit #{pageLength}, #{pageSize}

mapper.update:
update equipment set
 updateTime=#{timeString} ,
	termOfValidity=#{termOfValidity}

 updateTime=#{timeString} ,
	termOfValidity=#{termOfValidity} ,welfareStatus=1

 welfareStatus=2,updateTime=#{timeString} 
 welfareStatus=1,updateTime=#{timeString}
 welfareStatus=3,updateTime=#{timeString}
where userId=#{userId} and id=#{id}
 and welfareStatus not in(4,3)
 and welfareStatus=5 and authentiCationStatus=2

mapper.select:
select
 DISTINCT releaseTitle 
 DISTINCT serviceType 
from equipment where welfareStatus=1 and releaseType=#{releaseType}
and userId in (select userId from t_user_realname where detailed like
#{detailed})
and releaseTitle like #{releaseTitle}   
and serviceType like #{serviceType}

select count(*) from equipment where welfareStatus=1 and
releaseType=#{releaseType}
and userId in (select userId from t_user_realname where detailed like
#{detailed})
and releaseTitle like #{releaseTitle}   
and serviceType =#{serviceType}

select
id,servicFrequenc,releaseTitle,pictureUrl,serviceType,serviceDetailed
from equipment where welfareStatus=1 and releaseType=#{releaseType}
and userId in (select userId from t_user_realname where detailed like
#{detailed})
and releaseTitle like #{releaseTitle}   
and serviceType =#{serviceType}   
order by updateTime desc
limit #{pageLength}, #{pageSize}

