mapper.select:
select count(*) from menuandrenovationandpestcontrol where welfareStatus!=3 and userId=#{userId}   
and  welfareStatus=#{welfareStatus}     
and  releaseType=#{releaseType}

    select id,userId,evaluateid,servicFrequenc,releaseType,
releaseTitle,serviceIntroduction,remarks,pictureUrl,startPrice,
welfareStatus,authentiCationStatus,authentiCationFailure,
detailed,addressDetailed,contact,consigneeName,createTime,
updateTime,serviceDetailed,companyName
   from menuandrenovationandpestcontrol       
   where welfareStatus!=3 and userId=#{userId} 
    and  welfareStatus=#{welfareStatus}     
    and  releaseType=#{releaseType}     
   order by  updateTime desc
    limit #{pageLength}, #{pageSize}

 select count(*) from menuandrenovationandpestcontrol where authentiCationStatus in(1,3)   
and  contact=#{contact}     
 and  releaseType=#{releaseType}

mapper.update:
update menuandrenovationandpestcontrol set  
authentiCationStatus=#{authentiCationStatus},authentiCationFailure=#{authentiCationFailure},examineTime=#{examineTime},examineName=#{examineName},
welfareStatus=#{welfareStatus}
 where userId=#{userId}  and id=#{id}

update menuandrenovationandpestcontrol set  
updateTime=#{timeString}  
welfareStatus=2,updateTime=#{timeString} 
welfareStatus=1,updateTime=#{timeString}
welfareStatus=3,updateTime=#{timeString} 
 where userId=#{userId}  and id=#{id}
and welfareStatus not in(4,3)

   update menuandrenovationandpestcontrol set       
releaseTitle=#{releaseTitle},serviceIntroduction=#{serviceIntroduction},
remarks=#{remarks},pictureUrl=#{pictureUrl},startPrice=#{startPrice},
serviceDetailed=#{serviceDetailed},updateTime=#{updateTime},
authentiCationStatus=#{authentiCationStatus},welfareStatus=#{welfareStatus}

 where welfareStatus!=3 and userId=#{userId} and id=#{id}

mapper.select:
 select id,servicFrequenc,releaseTitle,pictureUrl,startPrice,serviceDetailed from menuandrenovationandpestcontrol where welfareStatus=1   and  releaseType=#{releaseType}
and detailed like #{detailed}
and  releaseTitle = #{releaseTitle}     
order by updateTime desc
 limit #{pageLength}, #{pageSize}
