mapper.select:
select * from order_common_offer
where
 orderFormId=#{orderFormId}
and saleUserId=#{saleUserId}
and commodStatus=1

mapper.update:
update order_common_offer set
updateTime=#{updateTime},commodStatus=2 
where orderFormId=#{orderFormId}

update order_common_offer set
updateTime=#{updateTime},commodStatus=2 
where orderFormId=#{orderFormId}  and commodStatus=1  and saleUserId=#{saleUserId}
