mapper.insert:
insert into t_user_realname (
    userId, provincesId, cityId, districtCountyId,addressDetailed,contact,consigneeName, authentiCationStatus,email,createTime,licenseUrl,detailed,eag,gender,userName,userType,examineName,examineTime,companyName
) values (
    #{userId}, #{provincesId}, #{cityId}, #{districtCountyId},#{addressDetailed},#{contact}, #{consigneeName},#{authentiCationStatus},#{email},#{createTime},#{licenseUrl},#{detailed},#{eag},#{gender},#{userName},#{userType},#{examineName},#{examineTime},#{companyName}
)

mapper.select:
select count(*) from t_user_realname where authentiCationStatus in(1,3) 
and  userName=#{userName}   
and  contact=#{contact}

 select * from t_user_realname where  authentiCationStatus in(1,3)   
 and  userName=#{userName}   
 and  contact=#{contact}   
order by  createTime limit #{pageLength}, #{pageSize}
