mapper.select:
select count(*) from release_welfare where  authentiCationStatus in(1,3) 
        and  userName=#{userName}   
and  contact=#{contact}

  select * from release_welfare where authentiCationStatus in(1,3)  
  and  userName=#{userName}   
  and  contact=#{contact}   

order by  createTime limit #{pageLength}, #{pageSize}

select count(*) from release_welfare where welfareStatus!=3 and userId=#{userId}   
    and  position=#{position}   
and  welfareStatus=#{welfareStatus}

        select id, userId,position ,number,welfare,salary,education,experience,describeOne,introductoryAward,gender,age,email,welfareStatus,isPublishContact, createTime,updateTime,
workingAddress,authentiCationStatus,authentiCationFailure,termOfValidity,contact,detailed,consigneeName from release_welfare where welfareStatus!=3 and userId=#{userId} 
        and  position=#{position}   
        and  welfareStatus=#{welfareStatus}     
       order by  updateTime desc
        limit #{pageLength}, #{pageSize}

select count(*) from release_welfare where welfareStatus=1 
and  detailed like #{detailed}   
and  position=#{position}

select * from release_welfare where welfareStatus=1 and id=#{id}
and  isPublishContact=1   
and  email is not null

        select id, userId,position ,number,welfare,salary,education,experience,describeOne,introductoryAward,gender,age,isPublishContact, createTime,updateTime,
workingAddress,termOfValidity,detailed from release_welfare where welfareStatus=1
        and  position=#{position}   
        and  detailed like #{detailed}   
        order by updateTime desc
        limit #{pageLength}, #{pageSize}

mapper.update:
 update release_welfare set  
 updateTime=#{timeString} ,termOfValidity=#{timeString}  
welfareStatus=1,termOfValidity=#{timeString} 
 welfareStatus=2,updateTime=#{timeString} 
 welfareStatus=1,updateTime=#{timeString},termOfValidity=#{timeString}
 welfareStatus=3,updateTime=#{timeString} 
  where userId=#{userId}  and id=#{id}
 and welfareStatus not in(4,5)

update release_welfare set  
authentiCationStatus=#{authentiCationStatus},authentiCationFailure=#{authentiCationFailure},examineTime=#{examineTime},examineName=#{examineName},
welfareStatus=#{welfareStatus}
 where userId=#{userId}  and id=#{id}
