mapper.select:
    select id,userId,releaseType,
releaseTitle,serviceIntroduction,remarks,pictureUrl,serviceDetailed,
welfareStatus,authentiCationStatus,authentiCationFailure,

detailed,addressDetailed,contact,consigneeName,companyName,createTime,
updateTime,termOfValidity,fouseSize
   from rent       
   where welfareStatus!=3 and userId=#{userId} 
    and  welfareStatus=#{welfareStatus}      
   order by  updateTime desc
    limit #{pageLength}, #{pageSize}

mapper.update:
update rent set  
authentiCationStatus=#{authentiCationStatus},authentiCationFailure=#{authentiCationFailure},examineTime=#{examineTime},examineName=#{examineName},
welfareStatus=#{welfareStatus}
 where userId=#{userId}  and id=#{id}

update rent set  
updateTime=#{timeString} , termOfValidity=#{termOfValidity}
updateTime=#{timeString} , termOfValidity=#{termOfValidity} ,welfareStatus=1
welfareStatus=2,updateTime=#{timeString} 
welfareStatus=1,updateTime=#{timeString}
welfareStatus=3,updateTime=#{timeString} 
 where userId=#{userId}  and id=#{id}
and welfareStatus not in(4,3)
 and welfareStatus=5 and authentiCationStatus=2

   update rent set       
pictureUrl=#{pictureUrl},authentiCationStatus=#{authentiCationStatus},
welfareStatus=#{welfareStatus},serviceDetailed=#{serviceDetailed},
remarks=#{remarks},serviceIntroduction=#{serviceIntroduction},fouseSize=#{fouseSize},
releaseTitle=#{releaseTitle},updateTime=#{updateTime},termOfValidity=#{termOfValidity}
 where welfareStatus!=3 and userId=#{userId} and id=#{id}

mapper.select:
 select id,releaseTitle,pictureUrl,detailed,fouseSize from rent where welfareStatus=1   and  releaseType=#{releaseType}
and detailed like #{detailed}
and  releaseTitle = #{releaseTitle}     
order by updateTime desc
 limit #{pageLength}, #{pageSize}
