mapper.select:
select count(*) from
shichang_wholesaleCommodity where welfareStatus!=3 and
releaseType=#{releaseType} and userId =#{userId}

select DISTINCT serviceType from shichang_wholesaleCommodity
where
releaseType=#{releaseType}

	and userId= #{userId}
	 and welfareStatus in(1,2,4,5) 
	and welfareStatus in(1,2) and startTime <
		#{dateString} and endTime > #{dateString} 
	and (welfareStatus =5 or endTime < #{dateString} )

	and welfareStatus in(1,2) and startTime >
		#{dateString}

	and welfareStatus= #{welfareStatus}   

and welfareStatus = 1 and startTime < #{dateString} and
	endTime > #{dateString}   
and serviceType like #{serviceType}

select DISTINCT serviceType from shichang_wholesaleCommodity
where
releaseType=#{releaseType}  
and userId in(
select userId from t_user_realname where  authentiCationStatus=2 and detailed=#{selectedOptions}
)

and welfareStatus=1 and  startTime < #{createTime} and endTime > #{createTime}
and serviceType like #{serviceType}   

and userId in(
select userId from t_user_realname where  authentiCationStatus=2 and companyName=#{companyName}
)

select count(*) from shichang_wholesaleCommodity
where
releaseType=#{releaseType}

	and userId= #{userId}
	 and welfareStatus in(1,2,4,5) 
	and welfareStatus in(1,2) and startTime <
		#{dateString} and endTime > #{dateString} 
	and (welfareStatus =5 or endTime < #{dateString} )

	and welfareStatus in(1,2) and startTime >
		#{dateString}

	and welfareStatus= #{welfareStatus}   

and welfareStatus = 1 and startTime < #{dateString} and
	endTime > #{dateString}   
and serviceType like #{serviceType}

select id,userId,releaseType,updateTime,createTime,pictureUrl,realNameId, serviceType,releaseTitle, detailed,addressDetailed,commodityPacking, cations,specifi, commodityJiage, commodityCountNo,commodityReserveNo,commoditySurplusNo, remarks,serviceIntroduction, reserve,deliveryType,deliveryCollect, startTime,endTime,authentiCationStatus,authentiCationFailure, welfareStatus, servicFrequenc,evaluateid from shichang_wholesaleCommodity where releaseType=#{releaseType}
and userId= #{userId} and welfareStatus in(1,2,4,5) and welfareStatus in(1,2) and startTime < #{dateString} and endTime > #{dateString} and (welfareStatus =5 or endTime < #{dateString} )
and welfareStatus in(1,2) and startTime > #{dateString}
and welfareStatus= #{welfareStatus}
and welfareStatus = 1 and startTime < #{dateString} and endTime > #{dateString} and serviceType like #{serviceType} order by updateTime desc limit #{pageLength}, #{pageSize}

select count(*) from shichang_wholesaleCommodity where
authentiCationStatus in(1,3) and releaseType=#{releaseType}
and userId
in(
select userId from t_user_realname
where authentiCationStatus=2
and contact=#{contact}   
and companyName=#{companyName}   
and detailed=#{detailed}   
)

select id,userId,releaseType,serviceType,
releaseTitle,detailed,addressDetailed,commodityPacking,cations,specifi,
commodityJiage,commodityCountNo,serviceIntroduction,
remarks,pictureUrl,deliveryType,deliveryCollect,
startTime,endTime,reserve,createTime,updateTime,
welfareStatus,authentiCationStatus,authentiCationFailure,
examineName
from shichang_wholesaleCommodity
where authentiCationStatus in(1,3) and
releaseType=#{releaseType}
and userId in(
select userId from
t_user_realname
where authentiCationStatus=2
and contact=#{contact}   
and companyName=#{companyName}   
and detailed=#{detailed}   
)
order by startTime desc
limit #{pageLength}, #{pageSize}

mapper.update:
update shichang_wholesaleCommodity set
authentiCationStatus=#{authentiCationStatus},authentiCationFailure=#{authentiCationFailure},examineTime=#{examineTime},examineName=#{examineName},
welfareStatus=#{welfareStatus}
where userId=#{userId} and id=#{id}

update shichang_wholesaleCommodity set
 updateTime=#{updateTime} 
 welfareStatus=2,updateTime=#{updateTime} 
 welfareStatus=1,updateTime=#{updateTime}
 welfareStatus=3,updateTime=#{updateTime}
where id=#{id}
 and welfareStatus=1
 and welfareStatus=2

mapper.select:
select count(*) 
from shichang_wholesaleCommodity

where 

releaseType=#{releaseType}  
and userId in(
select userId from t_user_realname where  authentiCationStatus=2 and detailed=#{selectedOptions}
)
and
welfareStatus=1 and  startTime < #{createTime} and endTime > #{createTime} 

and serviceType like #{serviceType}   
and userId in(
select userId from t_user_realname where  authentiCationStatus=2 and companyName=#{companyName}
)

select id,releaseType,serviceType,
releaseTitle,commodityPacking,commodityJiage,commoditySurplusNo,
reserve,specifi,cations,pictureUrl
from shichang_wholesaleCommodity
where 
releaseType=#{releaseType}  
and userId in(
select userId from t_user_realname where  authentiCationStatus=2 and detailed=#{selectedOptions}
)
and
welfareStatus=1 and  startTime < #{createTime} and endTime > #{createTime} 
	and serviceType like #{serviceType}   

and userId in(
select userId from t_user_realname where  authentiCationStatus=2 and companyName=#{companyName}
)   	
order by updateTime desc
limit #{pageLength}, #{pageSize}

select count(*) from shichang_wholesaleCommodity
where welfareStatus=1 and
addressDetailed=#{addressDetailed} and cations=#{cations} and commodityPacking=#{commodityPacking} and deliveryCollect=#{deliveryCollect} and deliveryType=#{deliveryType} and detailed=#{detailed} and id=#{id} and releaseTitle=#{releaseTitle} and releaseType=#{releaseType} and reserve=#{reserve} and serviceType=#{serviceType} and specifi=#{specifi} and commoditySurplusNo < #{commodityReserveNo} and startTime < #{updateTime} and endTime > #{updateTime}
