mapper.select:
select count(*) from
wineandtableware where welfareStatus!=3 and
releaseType=#{releaseType}
and userId =#{userId}

select count(*) from wineandtableware where welfareStatus!=3 and
userId=#{userId} and releaseType=#{releaseType}
and welfareStatus=#{welfareStatus}

mapper.update:
update wineandtableware set
authentiCationStatus=#{authentiCationStatus},authentiCationFailure=#{authentiCationFailure},examineTime=#{examineTime},examineName=#{examineName},
welfareStatus=#{welfareStatus}
where userId=#{userId} and id=#{id}

update wineandtableware set 
 updateTime=#{timeString},termOfValidity=#{termOfValidity}
 welfareStatus=2,updateTime=#{timeString} 
 welfareStatus=1,updateTime=#{timeString},termOfValidity=#{termOfValidity}

welfareStatus=3,updateTime=#{timeString}

welfareStatus=5,updateTime=#{timeString},examineName='过期定时任务'	
welfareStatus=3,updateTime=#{timeString},examineName='删除定时任务'
where userId=#{userId} and id=#{id}
 and welfareStatus not in(4,3)
 and welfareStatus =5 and authentiCationStatus=2
